targets.rawSql:
-- create view: views are defined in variable --
${pretask:raw}
${pretask_comp:raw}

-- visualization plot --
WITH base AS (
  SELECT COUNT(DISTINCT t4dataset_id) AS id_num, ${target_file} AS series
  FROM view_eval_flat
  WHERE ${topic_source_label_id_filter:raw}
),
comp AS (
  SELECT COUNT(DISTINCT t4dataset_id) AS id_num, ${compared_target_file} AS series
  FROM view_eval_flat_comp
  WHERE ${compared_topic_source_label_id_filter:raw}
)
SELECT * FROM base
UNION ALL
SELECT * FROM comp;

SELECT dist_h, label FROM view_eval_flat
WHERE
  ${topic_source_label_id_filter:raw}

